using DocuSign.Admin.Client;
using System;
using System.Collections.Generic;

namespace SdkNetCoreTests
{
    class TestConfig
    {
        public string IntegratorKey { get; set; }
        public string Host { get; set; }
        public ApiClient ApiClient { get; set; }
        //public Configuration Configuration { get; set; }
        public string AccountId { get; set; }
        public string RecipientEmail { get; set; }
        public string RecipientName { get; set; }
        public string TemplateRoleName { get; set; }
        public string TemplateId { get; set; }
        public string EnvelopeId { get; set; }
        public string ReturnUrl { get; set; }
        public string UserId { get; set; }
        public string OAuthBasePath { get; set; }
        public string PrivateKey { get; set; }
        public int ExpiresInHours { get; set; }

        public List<string> EnvelopeIdsList { get; set; }

        public string IntegratorKeyNoConsent { get; set; }

        public string PrivateKeyNoConsent { get; set; }

        public string BrandLogo { get; set; }

        public string BrandId { get; set; }

        public Guid? OrgId { get; set; }

        public long PermissionProfileId { get; set; }

        public TestConfig(string integratorKey = null, string host = null, string recipientEmail = null, string recipientName = null, string templateRoleName = null, string templateId = null, string returnUrl = null)
        {
            string userIdFromEnv = Environment.GetEnvironmentVariable("userid");
            string integratorKeyFromEnv = Environment.GetEnvironmentVariable("integratorkey");
            string templateIdFromEnv = Environment.GetEnvironmentVariable("templateid");
            string integratorKeyNoConsentFromEnv = Environment.GetEnvironmentVariable("integratorkeynoconsent");
            string brandIdfromEnv = Environment.GetEnvironmentVariable("brandid");

            this.Host = (host != null) ? host : "https://api-d.docusign.net/management";
            this.IntegratorKey = (integratorKey != null) ? integratorKey : integratorKeyFromEnv;

            this.RecipientEmail = (recipientEmail != null) ? recipientEmail : "docusignsdktest@mailinator.com";
            this.RecipientName = (recipientName != null) ? recipientName : "Pat Developer";

            this.TemplateRoleName = (templateRoleName != null) ? templateRoleName : "bob";
            this.TemplateId = (templateId != null) ? templateId : templateIdFromEnv;

            this.ReturnUrl = (returnUrl != null) ? returnUrl : "https://www.docusign.com/api";

            this.UserId = userIdFromEnv;
            this.OAuthBasePath = "account-d.docusign.com";
            this.PrivateKey = Environment.GetEnvironmentVariable("privatekey");
            this.ExpiresInHours = 1;

            this.EnvelopeIdsList = new List<string>();

            this.IntegratorKeyNoConsent = integratorKeyNoConsentFromEnv;
            this.PrivateKeyNoConsent = Environment.GetEnvironmentVariable("privatekeynoconsent");

            this.BrandLogo = "";
            this.BrandId = brandIdfromEnv;
            //this.Configuration = new Configuration();

            this.PermissionProfileId = 9305351;
        }
    }
}